import React, { Component } from 'react';

import './App.css';
import Control from 'react-leaflet-control';
import { Map, TileLayer,  GeoJSON, Marker, Popup, CircleMarker, Tooltip,
  LayersControl, LayerGroup, Circle } from 'react-leaflet';

import { library } from '@fortawesome/fontawesome-svg-core';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faDotCircle } from '@fortawesome/free-regular-svg-icons';
import { faRunning, faGamepad, faLaptopHouse, faHamburger, faFilm, faChartPie,
  faPeopleCarry } from '@fortawesome/free-solid-svg-icons';

import cities from './cities.json';
import sydney from './sydney.json';
import aus from './aus.json';

import activities from './activities.json';
import layers from './layers.json';
import { Container, Row, Col } from 'react-bootstrap';
import { LayerChooser } from './LayerChooser';

library.add(faDotCircle, faRunning, faGamepad, faLaptopHouse, faHamburger,
  faFilm, faChartPie, faPeopleCarry); 

const DEFAULT_ZOOM_LEVEL = 9;
const DEFAULT_CENTER = [-33.834905,150.9673231]; // Greater Sidney center
//const DEFAULT_CENTER =  = [-25.6101111,134.3547222] // Australia center
const DEFAULT_VIEWPORT = {
  center: DEFAULT_CENTER,
  zoom: DEFAULT_ZOOM_LEVEL
}

//https://data.gov.au/geoserver/nsw-suburb-locality-boundaries-psma-administrative-boundaries/wfs?request=GetFeature&typeName=ckan_91e70237_d9d1_4719_a82f_e71b811154c6&outputFormat=json

const layerStyle = {
  display: 'none',
  float: 'right'
}

const colors = ['#800026','#BD0026', '#E31A1C', '#FC4E2A', '#FD8D3C', '#FEB24C',
 '#FED976', '#FFEDA0'];
function getColor(d) {
  let rand = Math.floor(Math.random() * Math.floor(colors.length));
  return colors[rand];
}

class App extends Component {
  state = {
    viewport: DEFAULT_VIEWPORT,
    selected: 'exercise',
    selectedData: aus
  }

  resetView = () => {
    console.log("resetView");
    this.setState({ viewport: DEFAULT_VIEWPORT })
  }

  onViewportChanged = (viewport) => {
    this.setState({ viewport })
  }

  setSelected = (activity) => {
    console.log("setSelected: "+activity);
    this.setState({ selected: activity })
  }

  selectOverlayLayer = (layer) => {
    console.log("selectOverlayLayer: "+layer);
    this.setState({ selectedData: layer.data })
  }

  render() {

    console.log("returning: ")
    return ( //Sydney zoom level 9 aus 5
      <div>
        <h3 style={{ textAlign: "center" }}>Activities During #AustraliaLockdown</h3>
        <Map onViewportChanged={this.onViewportChanged} viewport={this.state.viewport} style={{ width: "80vh", height: "100vh" }}>
        <LayersControl position="topleft">
        <Control position="topleft">
      <button className="leaflet-control-layers btn-reset-view"
          onClick={ this.resetView }
          >
         <FontAwesomeIcon icon={ faDotCircle } size="lg"/>
        </button>
      </Control>
        <LayersControl.BaseLayer checked="checked" name="Black and White">
            <TileLayer
              attribution='&amp;copy <a href="http://osm.org/copyright">OpenStreetMap</a> contributors'
              url="https://tiles.wmflabs.org/bw-mapnik/{z}/{x}/{y}.png"
            />
          </LayersControl.BaseLayer>
        <LayersControl.BaseLayer name="Colored">
            <TileLayer
              attribution='&amp;copy <a href="http://osm.org/copyright">OpenStreetMap</a> contributors'
              url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
            />
          </LayersControl.BaseLayer>
          </LayersControl>
          { this.state.showMyComponent && 
            <LayerGroup style={layerStyle}>
              <Circle center={center} fillColor="blue" radius={200} />
              <Circle
                center={center}
                fillColor="red"
                radius={100}
                stroke={false}
              />
              <LayerGroup>
                <Circle
                  center={[51.51, -0.08]}
                  color="green"
                  fillColor="green"
                  radius={100}
                />
              </LayerGroup>
            </LayerGroup>
            }

        {cities.map((city, k) => {
            return (
              <CircleMarker
                key={k}
                center={[city["coordinates"][1], city["coordinates"][0]]}
                radius={5 * Math.log(city[this.state.selected] / 100000)}
                fillOpacity={0.9}
                stroke={true}
              >
                <Tooltip direction="right" offset={[-8, -2]} opacity={1}>
                  <strong>{city["name"]}</strong>
                  <span>{city["id"] + " " + city[this.state.selected]}</span>
                </Tooltip>
              </CircleMarker>
            );
          })}

<LayerGroup>
          <GeoJSON
          data={this.state.selectedData}
          style={(feature) => ({
            color: '#4a83ec',
            weight: 0.5,
            fillColor: getColor(feature),
            fillOpacity: 0.2,
          })}
        />
         <Marker position={[50, 10]}>
          <Popup>
            Popup for any custom information.
          </Popup>
        </Marker>
        </LayerGroup>
      <Control position="topright" >
        <Container>
        <Row>
        {activities.map(activity => (
          <Col className="mb-5">
            <LayerChooser layer={activity} key={activity.id} selectLayer={this.setSelected} />
          </Col>
        ))}
        </Row>
        </Container>
      </Control>
      <Control position="topright" >
        <Container>
        <Row>
        {layers.map(layer => (
          <Col className="mb-5">
            <LayerChooser layer={layer} key={layer.id} selectLayer={this.selectOverlayLayer} />
          </Col>
        ))}
        </Row>
        </Container>
      </Control>
</Map>

      </div>          
    );
  }
}

export default App;
