import React, { useState } from 'react';
import { Modal, Button } from 'react-bootstrap';

function Header() {
  const [show, setShow] = useState(false);
  const closeAbout = () => setShow(false);
  const showAbout = () => setShow(true);

  return (
   <>
      <header>
        <h1 className="text1">Activities in Sydney</h1>
        <h2 className="text2">#AustraliaLockdown</h2>
        <Button variant="outline-warning" className="text3" onClick={showAbout}>About this map</Button>
      </header>
      <Modal show={show} onHide={closeAbout}>
        <Modal.Header closeButton>
          <Modal.Title>Modal heading</Modal.Title>
        </Modal.Header>
        <Modal.Body>Woohoo, you're reading this text in a modal!</Modal.Body>
        <Modal.Footer>
          <Button variant="primary" onClick={closeAbout}>
            Save Changes
          </Button>
        </Modal.Footer>
      </Modal>
  </>
  )
}

export default Header;
