import React, { useState } from 'react';
import { Modal, Button } from 'react-bootstrap';

function Header() {
  const [show, setShow] = useState(false);
  const closeAbout = () => setShow(false);
  const showAbout = () => setShow(true);

  return (
   <>
      <header>
        <h1 className="text1">Activities in Sydney</h1>
        <h2 className="text2">#AustraliaLockdown</h2>
        <Button variant="outline-warning" className="text3" onClick={showAbout}>About this map</Button>
      </header>
      <Modal show={show} onHide={closeAbout}>
        <Modal.Header closeButton>
          <Modal.Title>Activities in Sydney #AustraliaLockdown</Modal.Title>
        </Modal.Header>
        <Modal.Body>
            <p>This map shows popular activities in Sydney during #LockdownAustralia based on Twitter data.</p>
          <p>Tapplication is developed for CCC assigment 2</p>
          </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={closeAbout}>
            Close
          </Button>
        </Modal.Footer>
      </Modal>
  </>
  )
}

export default Header;
