import React, { Component } from 'react';
import PropTypes from 'prop-types';

import { Button, Dropdown, DropdownButton } from 'react-bootstrap';

import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';

export class LayerChooser extends Component {

  selectLayer = (content) => {
    console.log("Layer: "+JSON.stringify(content));
    console.log("content.id: "+content.id)
    console.log("content.text: "+content.text)
 //   this.setState({ content })
    this.props.selectLayer(content)
  }

  render() {
    const { id, text, icon, variant, className, contents } = this.props.layer;
      return (
        <div>
          <DropdownButton className={ className } variant={variant} id="dropdown-basic-button" title={text}>
          {contents.map(content => (
                  <Dropdown.Item key={content.id} eventKey={content.id} onSelect={() => this.selectLayer(content)}>{content.text}</Dropdown.Item>
              ))
          }
           {/*  <Dropdown.Item eventKey="au">Australia</Dropdown.Item>
            <Dropdown.Item eventKey="nsw">New South Wales</Dropdown.Item>
           <Dropdown.Divider />
             <Dropdown.Item eventKey="nsw">New South Wales</Dropdown.Item>
            <Dropdown.Item eventKey="vic">Victoria</Dropdown.Item>
            <Dropdown.Item eventKey="qld">Queensland</Dropdown.Item>
            <Dropdown.Item eventKey="wa">Western Australia</Dropdown.Item>
            <Dropdown.Item eventKey="sa">South Australia</Dropdown.Item>
            <Dropdown.Item eventKey="act">Australian Capital Territory</Dropdown.Item>
            <Dropdown.Item eventKey="nt">Northern Territory</Dropdown.Item> */}
          </DropdownButton>
          {/* <Button variant={ variant }
            onClick={this.props.selectLayer.bind(this, id)}
            type="button"
            className={ className }>
              { text }
              <FontAwesomeIcon
                icon={ icon }
                size="2x"/>
            </Button> */}
        </div>
      )
    }
  }

LayerChooser.propTypes = {
  layer: PropTypes.object.isRequired
}

export default LayerChooser;
