import React, { Component, useState } from 'react';

import './App.css';
import { Map, TileLayer, GeoJSON, Marker, Popup, CircleMarker ,Tooltip,
  LayersControl,
  LayerGroup,
  Circle, FeatureGroup, Rectangle } from 'react-leaflet';

  import { library } from '@fortawesome/fontawesome-svg-core';
  import { faRunning, faGamepad, faLaptopHouse, faHamburger, faFilm } from '@fortawesome/free-solid-svg-icons';


import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';

import Control from 'react-leaflet-control';
import data2 from 'geojson-world-map';
import data1 from './sample.json';
import data3 from './us-states.json';
import data4 from './esa09.json';
import data8 from './file_copy.json';
import data from './data7984821864271563851.json';
import data0 from './census.json';
import data6 from './multipoly.json';
import data77 from './census.json';
import { LatLng } from 'leaflet';

import activities from './activities.json';
import { Container, Row, Col } from 'react-bootstrap';
import { PizzaCard } from './PizzaCard'

library.add(faRunning, faGamepad, faLaptopHouse, faHamburger, faFilm); 

const { BaseLayer, Overlay } = LayersControl

//const center = [-23.116667, 132.133333]
//const center = [ -33.807908, 151.0007289 ]
const center = [-33.88120790699997, 151.19471339100002]
const rectangle = [
  [51.49, -0.08],
  [51.5, -0.06],
]
const layerStyle = {
  display: 'none',
  float: 'right'
}
function getColor(d) {
    //d.properties.loc_pid.startsWith('NSW') ? '#800026' :
    return d > 1000 ? '#800026' :
           d > 500  ? '#BD0026' :
           d > 200  ? '#E31A1C' :
           d > 100  ? '#FC4E2A' :
           d > 50   ? '#FD8D3C' :
           d > 20   ? '#FEB24C' :
           d > 10   ? '#FED976' :
                      '#FFEDA0';
}

class App extends Component {
  state = {
    todos: [],
    showMyComponent: false,
    ordered: false
  }

  setOrdered(ordered) {
    this.setState({ordered: true})
  }


  displayConfirmation() {
    this.setOrdered(true);

    setTimeout(() => {
      this.setOrdered(false);
    }, 3000);
  }
  render() {

    console.log("returning: ")
    return (
      <div>
        <h3 style={{ textAlign: "center" }}>Most Populous Cities in Asia</h3>
        <Map center={center} zoom={13} style={{ height: "900px" }}>

        <LayersControl position="topright">
        <BaseLayer checked="checked" name="Black and White">
            <TileLayer
              attribution='&amp;copy <a href="http://osm.org/copyright">OpenStreetMap</a> contributors'
              url="https://tiles.wmflabs.org/bw-mapnik/{z}/{x}/{y}.png"
            />
          </BaseLayer>
        <BaseLayer name="Colored">
            <TileLayer
              attribution='&amp;copy <a href="http://osm.org/copyright">OpenStreetMap</a> contributors'
              url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
            />
          </BaseLayer>



                     {/* {data.city.map((city, k) => {
            return (
              <CircleMarker
                key={k}
                center={[city["coordinates"][1], city["coordinates"][0]]}
                radius={20 * Math.log(city["population"] / 10000000)}
                fillOpacity={0.5}
                stroke={false}
              >
                <Tooltip direction="right" offset={[-8, -2]} opacity={1}>
                  <span>{city["name"] + ": " + "Population" + " " + city["population"]}</span>
                </Tooltip>
              </CircleMarker>
            );
          })} */}
          <Overlay name="aneh 1">
          <GeoJSON
          data={data}
          coordsToLatLng={function (coords) {
            //                    latitude , longitude, altitude
            //return new L.LatLng(coords[1], coords[0], coords[2]); //Normal behavior
            console.log("coords0: " + coords+", t:"+typeof(coords)+", i:")
            //return new LatLng(coords[0], coords[1], coords[2]);
            //<LatLng>3,4</LatLng>
           // console.log("coords1: " + new LatLng(coords[1], coords[0], coords[2])+", t:"+typeof(coords)+", i:")
            return new LatLng(coords[1], coords[0], coords[2]);
            //return <LatLng lat={coords[0]} lng={coords[1]} latitude={coords[0]} longitude={coords[1]}></LatLng>
       }}
          style={(feature) => ({
            color: '#4a83ec',
            weight: 0.5,
            fillColor: '#4a83ec',//getColor(feature),
            fillOpacity: 0.7,
          })}
        />
         <Marker position={[50, 10]}>
          <Popup>
            Popup for any custom information.
          </Popup>
        </Marker>
          </Overlay>
          <Overlay name="aneh 2">
            <Marker position={center}>
              <Popup>
                A pretty CSS3 popup. <br /> Easily customizable.
              </Popup>
            </Marker>
          </Overlay>
          <Overlay name="aneh">
            <Marker position={center}>
              <Popup>
                A pretty CSS3 popup. <br /> Easily customizable.
              </Popup>
            </Marker>
          </Overlay>
           <Overlay name="Marker with popup">
            <Marker position={center}>
              <Popup>
                A pretty CSS3 popup. <br /> Easily customizable.
              </Popup>
            </Marker>
          </Overlay>
          <Overlay name="Feature group">
            <FeatureGroup color="purple">
              <Popup>Popup in FeatureGroup</Popup>
              <Circle center={[51.51, -0.06]} radius={200} />
              <Rectangle bounds={rectangle} />
            </FeatureGroup>
          </Overlay>
          </LayersControl>
          { this.state.showMyComponent && 
            <LayerGroup style={layerStyle}>
              <Circle center={center} fillColor="blue" radius={200} />
              <Circle
                center={center}
                fillColor="red"
                radius={100}
                stroke={false}
              />
              <LayerGroup>
                <Circle
                  center={[51.51, -0.08]}
                  color="green"
                  fillColor="green"
                  radius={100}
                />
              </LayerGroup>
            </LayerGroup>
            }

      <Control position="topleft" >
        <button 
          onClick={ () => this.setState({showMyComponent: !this.state.showMyComponent}) }
        >
          Reset View
        </button>
        {activities.map(data => (
          <Col xs={3} className="mb-5" key={`${data.id}`}>
            <PizzaCard data={data} setOrdered={this.displayConfirmation} />
          </Col>
        ))}
      </Control>
</Map>

      </div>
    );
  }
}

export default App;
