import React, { Component } from 'react';

import './App.css';

import { library } from '@fortawesome/fontawesome-svg-core';
import { faDotCircle } from '@fortawesome/free-regular-svg-icons';
import { faRunning, faGamepad, faLaptopHouse, faHamburger, faFilm, faGraduationCap,
  faPeopleCarry} from '@fortawesome/free-solid-svg-icons';

import Header from './components/Header';
import MainMap from './components/MainMap';

library.add(faDotCircle, faRunning, faGamepad, faLaptopHouse, faHamburger,
  faFilm, faGraduationCap, faPeopleCarry); 

const DEFAULT_ZOOM_LEVEL = 9; // Greater Sydney zoom level
const DEFAULT_CENTER = [-33.834905,150.9673231]; // Greater Sidney center

const AU_ZOOM_LEVEL = 5; // Australia zoom level
const AU_CENTER = [-25.6101111,134.3547222] // Australia center

const DEFAULT_VIEWPORT = {
  center: DEFAULT_CENTER,
  zoom: DEFAULT_ZOOM_LEVEL
}

const AU_VIEWPORT = {
  center: AU_CENTER,
  zoom: AU_ZOOM_LEVEL
}

//https://data.gov.au/geoserver/nsw-suburb-locality-boundaries-psma-administrative-boundaries/wfs?request=GetFeature&typeName=ckan_91e70237_d9d1_4719_a82f_e71b811154c6&outputFormat=json

const layerStyle = {
  display: 'none',
  float: 'right'
}

const colors = ['#800026','#BD0026', '#E31A1C', '#FC4E2A', '#FD8D3C', '#FEB24C',
 '#FED976', '#FFEDA0'];
function getColor(d) {
  let rand = Math.floor(Math.random() * Math.floor(colors.length));
  return colors[rand];
}

class App extends Component {
  state = {
    viewport: DEFAULT_VIEWPORT,
    selected: 'exercise',
    area: "nsw"
  }

  componentDidMount(){
    document.title = "Activities during #AustraliaLockdown"
  }

  resetView = () => {
    this.setState({ viewport: DEFAULT_VIEWPORT })
  }

  onViewportChanged = (viewport) => {
    this.setState({ viewport })
  }

  setSelected = (activity) => {
    this.setState({ selected: activity })
  }

  selectOverlayLayer = (layer) => {
    this.setState({ selectedData: layer.data })
  }

  selectArea = (area) => {
    this.setState({ area })
    if(area === "au") {
      this.setState({ viewport: AU_VIEWPORT })
    } else {
      this.setState({ viewport: DEFAULT_VIEWPORT })
    }
    console.log("select area "+area)
  }

  render() {
    return (
      <>
          <Header selectArea={this.selectArea}/>
          <div id="container">
          <MainMap vp={this.state.viewport} area={this.state.area} />
           </div>
           </>
    );
  }
}

export default App;
