import React, { Component } from "react";
import { Map, TileLayer,  GeoJSON, Marker, Popup, CircleMarker, Tooltip,
  LayersControl, LayerGroup, Circle } from "react-leaflet";
import Control from "react-leaflet-control";

import { library } from "@fortawesome/fontawesome-svg-core";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faDotCircle } from "@fortawesome/free-regular-svg-icons";
import { faRunning, faHamburger,
  faFilm } from "@fortawesome/free-solid-svg-icons";

import { ButtonGroup } from "react-bootstrap";

import axios from "axios";

import activities from "../activities.json";
import layers from "../layers.json";

import LayerChooser from "./LayerChooser";
import ActivityChooser from "./ActivityChooser";
import Legend from "./Legend";

library.add(faDotCircle, faRunning, faHamburger, faFilm); 

const COLORS = ["#f7fbff", "#deebf7", "#c6dbef", "#9ecae1", "#6baed6",
                "#4292c6", "#2171b5", "#08519c", "#08306b", "#05234d"]

//const SERVICE_ROOT_URL = "http://localhost/json/data";
const SERVICE_ROOT_URL = "http://localhost:5000";
//const SERVICE_ROOT_URL = "http://progo.lastanto.com/json/data";

class MainMap extends Component {

  activityData = {}
  indexData = {}

  state = {
    viewport: this.props.vp,
    selectedActivity: null,
    loading: {
      "nsw": true,  
      "aus": true
      },
    selectedContentId: 0,
    selectedContent: null,
    currentArea: "aus"
  }

  componentDidMount(){
    let nswRequests = [
      axios.get(SERVICE_ROOT_URL + "/summaries/nsw", {crossDomain: true }),
      axios.get(SERVICE_ROOT_URL + "/indices/nsw", {crossDomain: true })
    ];

    let ausRequests = [
      axios.get(SERVICE_ROOT_URL + "/summaries/aus", {crossDomain: true }),
      axios.get(SERVICE_ROOT_URL + "/indices/aus", {crossDomain: true })
    ];

axios
  .all(nswRequests)
  .then(
    axios.spread((...responses) => {
      this.activityData["nsw"] = responses[0].data;
      this.indexData["nsw"] = responses[1].data;
      let loading = {...this.state.loading};
      loading.nsw = false;
      this.setState({ loading });
      console.log("NSW data loaded.");
      console.log(this.state.loading)
    })
  )
  .catch(errors => {

    // let activity = require("../data/nsw-summary.json");
    // let index = require("../data/nsw-index.json");

    // ACTIVITY_DATA["nsw"] = activity;
    // INDEX_DATA["nsw"] = index;
    // let loading = {...this.state.loading};
    // loading.nsw = false;
    // this.setState({ loading });
    // console.log("NSW data loaded from file.");
    // console.log(this.state.loading)
    // console.log(activity)

  });

  axios
  .all(ausRequests)
  .then(
    axios.spread((...responses) => {
      this.activityData["aus"] = responses[0].data;
      this.indexData["aus"] = responses[1].data;
      let loading = {...this.state.loading};
      loading.aus = false;
      this.setState({ loading });
      console.log("AU data loaded.");
      console.log(this.state.loading)
    })
  )
  .catch(errors => {
    // let activity = require("../data/au-summary.json");
    // let index = require("../data/au-index.json");

    // ACTIVITY_DATA["au"] = activity;
    // INDEX_DATA["au"] = index;
    // let loading = {...this.state.loading};
    // loading.au = false;
    // this.setState({ loading });
    // console.log("AU data loaded from file.");
    // console.log(this.state.loading)
    // console.log(activity)

  });

  }

  getColor = (d) => {
    //layerId === 2
    //  ? d = d / 1000
    //  : d = d * 100
    d = d/1000;

    //console.log('color: '+d)
    return d > 90
      ? COLORS[9]
      : d > 80
      ? COLORS[8]
      : d > 70
      ? COLORS[7]
      : d > 60
      ? COLORS[6]
      : d > 50
      ? COLORS[5]
      : d > 40
      ? COLORS[4]
      : d > 30
      ? COLORS[3]
      : d > 20
      ? COLORS[2]
      : d > 10
      ? COLORS[1]
      : COLORS[0];
  };

  resetView = () => {
    console.log(this.props.vp)
    this.setState({ viewport: this.props.vp })
  }

  onViewportChanged = (viewport) => {
    this.setState({ viewport })
  }

  setSelectedActivity = (activity) => {
    console.log("selectActiviy: "+activity)
    this.setState({ selectedActivity: activity })
  }

  changeViewPort(area) {
    console.log("area: "+area)
    if(this.state.currentArea !== area ) {
      this.setState({ currentArea:area })
  //    this.state.activityData = ACTIVITY_DATA[area]
  //    this.state.indexData = INDEX_DATA[area]
      this.resetView()
      console.log("reset")
    }
  }

  selectLayer = (content) => {
    console.log(this.state.currentArea+" :id:"+content.id+"->")
    this.setState({ selectedContentId:content.id,
                    selectedContent:content })
  }

  render() {
    setInterval(() => this.changeViewPort(this.props.area), 2000);
    return (
        <Map id="map" onViewportChanged={this.onViewportChanged}
          viewport={this.state.viewport}>
          <LayersControl position="topleft">
          <Control position="topleft">
            <button
              className="leaflet-control-layers feat-btn-reset-view"
              onClick={ this.resetView }
            >
          <FontAwesomeIcon icon={ faDotCircle } size="lg"/>
          </button>
          </Control>
          <LayersControl.BaseLayer
            checked="checked"
            name="Black and White">
            <TileLayer
              attribution='&amp;copy <a href="http://osm.org/copyright">
                OpenStreetMap</a> contributors'
              url="https://tiles.wmflabs.org/bw-mapnik/{z}/{x}/{y}.png"
              />
            </LayersControl.BaseLayer>
          <LayersControl.BaseLayer
            name="Colored">
            <TileLayer
              attribution='&amp;copy <a href="http://osm.org/copyright">
                OpenStreetMap</a> contributors'
              url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
            />
          </LayersControl.BaseLayer>
          </LayersControl>
          <LayerGroup>
          {this.indexData[this.state.currentArea] ? (
            <GeoJSON test={console.log("Geo: "+this.state.currentArea)}
              key={this.state.currentArea}
              data={this.indexData[this.state.currentArea]}
              style={(feature) => ({
              color: '#4a83ec',
              weight: 0.5,
              fillColor: this.getColor(feature['properties'][this.state.selectedContentId]),
              fillOpacity: 0.85
            })}
          />
          ) : (<></>)}
          </LayerGroup>
          <LayerGroup>
          {this.state.selectedActivity && this.activityData[this.state.currentArea]["summary"] ? (
          this.activityData[this.state.currentArea]["summary"].map((city, k) => {
              return (
                <CircleMarker
                  color="#dbae28"
                  key={k}
                  center={[city["point"][1], city["point"][0]]}
                  //radius={5 * Math.log(city[this.state.selected] / 100000)}
                  radius={5 * Math.log(city[this.state.selectedActivity+"_total"])}
                  fillOpacity={0.3}
                  stroke={true}
                  weight={2}
                >
                  <Tooltip direction="right" offset={[-8, -2]} opacity={1}>
                    <strong>{city["name"]}: </strong>
                    <span>{ parseInt(city[this.state.selectedActivity+"_total"]).toLocaleString("en-AU")} ({+(100*Math.round(city[this.state.selectedActivity]+"e+4") + "e-4")}%)</span>
                  </Tooltip>
                </CircleMarker>
              );
            })):(<></>)}
            </LayerGroup>
            {this.state.selectedContent ? (
              <>
          <Legend getColor={this.getColor} />
          <Control position="bottomleft" >
            <span><strong>{this.state.selectedContent  ? this.state.selectedContent["text"] : ""} </strong></span>  
          </Control>
          </>
            ):(<></>)}
          {!this.state.loading[this.state.currentArea] ? (
            <>
          <Control position="topright" >
            <ButtonGroup toggle>
              {layers.map(layer => (
                  <LayerChooser layer={layer} key={layer.id} variant={layer.variant} className={layer.className} selectLayer={this.selectLayer} />
              ))}
            </ButtonGroup>
            <br/><br/><br/>
          </Control>
          <Control position="topright" >
            <ButtonGroup vertical>
              {activities.map(activity => (
                <ActivityChooser
                  activity={activity}
                  key={activity.id}
                   selectActivity={this.setSelectedActivity}
                />
             ))}
            </ButtonGroup>
          </Control>
                </>
          ) : (

            <Control position="topright" >
              <span><strong>Loading data...</strong></span>
            </Control>
              )
              }
        </Map>

    );
  }
}

export default MainMap;
