import React, { Component } from 'react';
import PropTypes from 'prop-types';

import { Button, Dropdown, DropdownButton } from 'react-bootstrap';

import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';

export class LayerChooser extends Component {
  render() {
    const { id, text, icon, variant, className } = this.props.layer;
      return (
        <div>
                    <DropdownButton className="area-selector" variant="outline-warning" onSelect={e => {this.selectArea(e)}}  id="dropdown-basic-button" title="Select Area">
            <Dropdown.Item eventKey="au">Australia</Dropdown.Item>
            <Dropdown.Item eventKey="nsw">New South Wales</Dropdown.Item>
            {/*<Dropdown.Divider />
             <Dropdown.Item eventKey="nsw">New South Wales</Dropdown.Item>
            <Dropdown.Item eventKey="vic">Victoria</Dropdown.Item>
            <Dropdown.Item eventKey="qld">Queensland</Dropdown.Item>
            <Dropdown.Item eventKey="wa">Western Australia</Dropdown.Item>
            <Dropdown.Item eventKey="sa">South Australia</Dropdown.Item>
            <Dropdown.Item eventKey="act">Australian Capital Territory</Dropdown.Item>
            <Dropdown.Item eventKey="nt">Northern Territory</Dropdown.Item> */}
          </DropdownButton>
          <Button variant={ variant }
            onClick={this.props.selectLayer.bind(this, id)}
            type="button"
            className={ className }>
              { text }
              <FontAwesomeIcon
                icon={ icon }
                size="2x"/>
            </Button>
        </div>
      )
    }
  }

LayerChooser.propTypes = {
  layer: PropTypes.object.isRequired
}

export default LayerChooser;
