import React, { Component } from 'react';

import Control from 'react-leaflet-control';
import { Map, TileLayer,  GeoJSON, Marker, Popup, CircleMarker, Tooltip,
  LayersControl, LayerGroup, Circle } from 'react-leaflet';

import { library } from '@fortawesome/fontawesome-svg-core';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faDotCircle } from '@fortawesome/free-regular-svg-icons';
import { faRunning, faGamepad, faLaptopHouse, faHamburger, faFilm, faChartPie,
  faPeopleCarry } from '@fortawesome/free-solid-svg-icons';

//TODO: change to list
//import cities from '../cities.json';
import cities from '../data/nsw-summary.json';
import cities2 from '../data/aus-summary.json';

import { ButtonGroup } from 'react-bootstrap';
//import aus from '../aus.json';

import auIndex from '../data/au-index.json';
import nswIndex from '../data/nsw-index.json';
//import aus from '../data/data6207385239988214929.json';

import activities from '../activities.json';
import layers from '../layers.json';
import LayerChooser from './LayerChooser';
import Legend from "./Legend";

library.add(faDotCircle, faRunning, faGamepad, faLaptopHouse, faHamburger,
  faFilm, faChartPie, faPeopleCarry); 

const ACTIVITY_DATA = {
  "au": cities2,
  "nsw": cities
}

const INDEX_DATA = {
  "au": auIndex,
  "nsw": nswIndex
}

const DEFAULT_ZOOM_LEVEL = 9; // Greater Sydney zoom level
const DEFAULT_CENTER = [-33.834905,150.9673231]; // Greater Sidney center

//const DEFAULT_ZOOM_LEVEL = 5; // Australia zoom level
//const DEFAULT_CENTER =  = [-25.6101111,134.3547222] // Australia center
const DEFAULT_VIEWPORT = {
  center: DEFAULT_CENTER,
  zoom: DEFAULT_ZOOM_LEVEL
}

//https://data.gov.au/geoserver/nsw-suburb-locality-boundaries-psma-administrative-boundaries/wfs?request=GetFeature&typeName=ckan_91e70237_d9d1_4719_a82f_e71b811154c6&outputFormat=json

const layerStyle = {
  display: 'none',
  float: 'right'
}

const colors = ['#f7fcf5','#e5f5e0','#c7e9c0','#a1d99b', '#74c476','#41ab5d',
                '#238b45', '#005a32'];

function getColor0(d, idx) { // TODO: Replace with the real implementation
   // let color = (90 - d['properties']['Name'].toUpperCase().codePointAt(idx)) % 8;
  let color = Math.floor(Math.random() * Math.floor(colors.length));
  return colors[color];
}

const getColor = d => {
  console.log('color: '+d)
  return d > 90
    ? colors[8]
    : d > 80
    ? colors[7]
    : d > 70
    ? colors[6]
    : d > 60
    ? colors[5]
    : d > 50
    ? colors[4]
    : d > 40
    ? colors[3]
    : d > 30
    ? colors[2]
    : d > 20
    ? colors[1]
    : colors[0];
};

class MainMap extends Component {
  state = {
    viewport: this.props.vp,
    selected: 'exercise',
//    selectedData: nswIndex,
    selectedLayerId: 0,
    currentArea: "nsw",
//    activityData: ACTIVITY_DATA["nsw"],
//    indexData: INDEX_DATA["nsw"]
  }

  resetView = () => {
    console.log(this.props.vp)
    this.setState({ viewport: this.props.vp })
  }

  onViewportChanged = (viewport) => {
    this.setState({ viewport })
  }

  setSelected = (activity) => {
    this.setState({ selected: activity })
  }

  changeViewPort(area) {
    console.log("area: "+area)
    if(this.state.currentArea !== area ) {
      this.state.currentArea = area
  //    this.state.activityData = ACTIVITY_DATA[area]
  //    this.state.indexData = INDEX_DATA[area]
      this.resetView()
      console.log("reset")
    }
  }

  selectOverlayLayer = (layerId) => {
    console.log("id:"+layerId+"->")
    console.log(layers[layerId])
    this.setState({ selectedLayerId:layerId })
  }

  render() {
    setInterval(() => this.changeViewPort(this.props.area), 2000);
    return (
        <Map id="map" onViewportChanged={this.onViewportChanged}
          viewport={this.state.viewport}>
          <LayersControl position="topleft">
          <Control position="topleft">
            <button
              className="leaflet-control-layers feat-btn-reset-view"
              onClick={ this.resetView }
            >
          <FontAwesomeIcon icon={ faDotCircle } size="lg"/>
          </button>
          </Control>
          <LayersControl.BaseLayer
            checked="checked"
            name="Black and White">
            <TileLayer
              attribution='&amp;copy <a href="http://osm.org/copyright">
                OpenStreetMap</a> contributors'
              url="https://tiles.wmflabs.org/bw-mapnik/{z}/{x}/{y}.png"
              />
            </LayersControl.BaseLayer>
          <LayersControl.BaseLayer
            name="Colored">
            <TileLayer
              attribution='&amp;copy <a href="http://osm.org/copyright">
                OpenStreetMap</a> contributors'
              url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
            />
          </LayersControl.BaseLayer>
          </LayersControl>
          <LayerGroup>
            <GeoJSON
            data={INDEX_DATA[this.state.currentArea]}
              style={(feature) => ({
              color: '#4a83ec',
              weight: 0.5,
              fillColor: getColor(100*feature['properties']['employment_ratio']),
              fillOpacity: 0.6
            })}
          />
          <Marker position={[50, 10]}>
            <Popup>
              Popup for any custom information.
            </Popup>
          </Marker>
          </LayerGroup>
          <LayerGroup>
          {ACTIVITY_DATA[this.state.currentArea].map((city, k) => {
              return (
                <CircleMarker
                  color="#dbae28"
                  key={k}
                  center={[city["point"][1], city["point"][0]]}
                  //radius={5 * Math.log(city[this.state.selected] / 100000)}
                  radius={5 * Math.log(city[this.state.selected+"_total"])}
                  fillOpacity={0.5}
                  stroke={true}
                  weight={5}
                >
                  <Tooltip direction="right" offset={[-8, -2]} opacity={1}>
                    <strong>{city["name"]}: </strong>
                    <span>{city[this.state.selected+"_total"]} ({+(Math.round(city[this.state.selected]+"e+3") + "e-3")}%)</span>
                  </Tooltip>
                </CircleMarker>
              );
            })}
            </LayerGroup>
          <Legend title="" desc="Employment ratio (%)" />
          <Control position="topright" >
            <ButtonGroup toggle>
              {layers.map(layer => (
                  <LayerChooser layer={layer} key={layer.id} variant={layer.variant} className={layer.className} selectLayer={this.selectOverlayLayer} />
              ))}
            </ButtonGroup>
          </Control>
          <Control position="topright" >
              <ButtonGroup vertical>
                {activities.map(activity => (
                  <LayerChooser
                    layer={activity}
                    key={activity.id}
                    selectLayer={this.setSelected}
                  />
              ))}
              </ButtonGroup>
          </Control>
        </Map>

    );
  }
}

export default MainMap;
